void dibujarMapa1(int Mapa1[FILA][COLUMNA]) {
    for (int f = 0; f < FILA; f++) {
        for (int c = 0; c < COLUMNA; c++) {
            Console::SetCursorPosition(c, f);
            if (Mapa1[f][c] == 0) Console::ForegroundColor = ConsoleColor::Green;
            if (Mapa1[f][c] == 1) Console::ForegroundColor = ConsoleColor::Black;
            if (Mapa1[f][c] == 2)Console::ForegroundColor = ConsoleColor::DarkYellow;
            if (Mapa1[f][c] == 3)Console::ForegroundColor = ConsoleColor::DarkYellow;
            cout << (char)219;
        }
    }
}